function setCookie(e,o,n){var t=new Date;t.setDate(t.getDate()+n),document.cookie=e+"="+escape(o)+"; expires="+t.toUTCString(),console.log("cookie "+e+" created")}function getCookie(e){var o=new RegExp(e+"=(?<value>[0-9a-zA-Zs]+);?"),n=document.cookie.match(o);if(n)return n.groups.value}function showCookieBanner(e){getCookie(e)||document.getElementById(e).classList.add("active")}function hideCookieBanner(e){document.getElementById(e).classList.remove("active"),setCookie(e,!0,5)}showCookieBanner("banner-1"),showCookieBanner("banner-2"),showCookieBanner("banner-3");